import React, { useState, useEffect } from 'react';

function FileUpload() {
    const [selectedFiles, setSelectedFiles] = useState(null);
    const [folderName, setFolderName] = useState('');
    const [responseMessage, setResponseMessage] = useState('');
    const [fileList, setFileList] = useState([]);
    const [folders, setFolders] = useState([]); // Для списка существующих папок
    const [isFolderExists, setIsFolderExists] = useState(false);

    useEffect(() => {
        // Загружаем список существующих папок при монтировании компонента
        fetchFolders();
    }, []);

    const fetchFolders = async () => {
        try {
            const response = await fetch('http://26.105.9.189:5000/folders');
            if (response.ok) {
                const data = await response.json();
                setFolders(data.folders);
            } else {
                setResponseMessage('Ошибка при загрузке папок.');
            }
        } catch (error) {
            console.error('Ошибка:', error);
            setResponseMessage('Ошибка при соединении с сервером.');
        }
    };

    const handleFileChange = (event) => {
        setSelectedFiles(event.target.files);
    };

    const handleFolderChange = (event) => {
        setFolderName(event.target.value);
        fetchFileList(event.target.value); // После выбора папки загружаем её содержимое
    };

    const fetchFileList = async (folderName) => {
        try {
            const response = await fetch(`http://26.105.9.189:5000/files/${folderName}`);
            if (response.ok) {
                const data = await response.json();
                setFileList(data.files);
                setIsFolderExists(true);
            } else {
                setIsFolderExists(false);
            }
        } catch (error) {
            console.error('Ошибка:', error);
            setResponseMessage('Ошибка при соединении с сервером.');
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        if (!selectedFiles || !folderName) {
            setResponseMessage('Пожалуйста, выберите папку и файлы для загрузки.');
            return;
        }

        const formData = new FormData();
        formData.append('folderName', folderName);

        for (let i = 0; i < selectedFiles.length; i++) {
            formData.append('files', selectedFiles[i]);
        }

        try {
            const response = await fetch('http://26.105.9.189:5000/upload', {
                method: 'POST',
                body: formData
            });

            if (response.ok) {
                const data = await response.json();
                setResponseMessage(data.message);
                fetchFileList(folderName); // Обновляем список файлов
            } else {
                setResponseMessage('Ошибка при загрузке файлов.');
            }
        } catch (error) {
            console.error('Ошибка:', error);
            setResponseMessage('Ошибка при соединении с сервером.');
        }
    };

    const deleteFile = async (fileName) => {
        try {
            const response = await fetch(`http://26.105.9.189:5000/files/${folderName}/${fileName}`, {
                method: 'DELETE'
            });

            if (response.ok) {
                const data = await response.json();
                setResponseMessage(data.message);
                fetchFileList(folderName); // Обновляем список файлов
            } else {
                setResponseMessage('Ошибка при удалении файла.');
            }
        } catch (error) {
            console.error('Ошибка:', error);
            setResponseMessage('Ошибка при соединении с сервером.');
        }
    };

    const downloadFile = (fileName) => {
        const link = document.createElement('a');
        link.href = `http://26.105.9.189:5000/files/${folderName}/download/${fileName}`;
        link.download = fileName;
        link.click();
    };

    return (
        <div>
            <h1>Загрузка файлов</h1>

            {/* Выбор папки */}
            <div>
                <label htmlFor="folderName">Выберите папку:</label>
                <select id="folderName" value={folderName} onChange={handleFolderChange}>
                    <option value="">Выберите папку</option>
                    {folders.map((folder, index) => (
                        <option key={index} value={folder}>
                            {folder}
                        </option>
                    ))}
                </select>
            </div>

            {responseMessage && <p>{responseMessage}</p>}

            {/* Если папка существует, показываем файлы и форму загрузки */}
            {isFolderExists !== null && (
                <div>
                    <h2>Файлы в папке "{folderName}":</h2>
                    {isFolderExists ? (
                        <ul>
                            {fileList.length > 0 ? (
                                fileList.map((file, index) => (
                                    <li key={index}>
                                        {file}
                                        <button onClick={() => downloadFile(file)} style={{ marginLeft: '10px' }}>
                                            Скачать
                                        </button>
                                        <button onClick={() => deleteFile(file)} style={{ marginLeft: '10px' }}>
                                            Удалить
                                        </button>
                                    </li>
                                ))
                            ) : (
                                <li>Нет файлов в этой папке.</li>
                            )}
                        </ul>
                    ) : (
                        <p>Папки с таким именем не существует. Она будет создана при загрузке файлов.</p>
                    )}

                    <form onSubmit={handleSubmit}>
                        <div>
                            <input type="file" multiple onChange={handleFileChange} />
                        </div>
                        <div>
                            <button type="submit">Загрузить файлы</button>
                        </div>
                    </form>
                </div>
            )}
        </div>
    );
}

export default FileUpload;
