import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isRegistering, setIsRegistering] = useState(false);
    const [folders, setFolders] = useState([]);
    const [files, setFiles] = useState([]);
    const [currentFolder, setCurrentFolder] = useState(''); // Текущая папка
    const [loading, setLoading] = useState(false);

    // Загружаем данные из localStorage при монтировании компонента
    useEffect(() => {
        const storedUsername = localStorage.getItem('username');
        const storedPassword = localStorage.getItem('password');

        if (storedUsername && storedPassword) {
            setUsername(storedUsername);
            setPassword(storedPassword);
            setIsLoggedIn(true);
            loadFolders(); // Загружаем папки после восстановления состояния
        }
    }, []);

    // Регистрация нового пользователя
    const handleRegister = async () => {
        try {
            const response = await axios.post('http://26.105.9.189:5000/register', { username, password });
            setMessage(response.data.message);
            setIsRegistering(false);
        } catch (error) {
            setMessage(error.response?.data?.message || 'Ошибка регистрации');
        }
    };

    // Вход пользователя
    const handleLogin = async () => {
        try {
            const response = await axios.post('http://26.105.9.189:5000/login', { username, password });
            setMessage(response.data.message);
            setIsLoggedIn(true);
            localStorage.setItem('username', username); // Сохраняем имя пользователя
            localStorage.setItem('password', password); // Сохраняем пароль
            loadFolders(); // Загружаем папки после входа
        } catch (error) {
            setMessage(error.response?.data?.message || 'Ошибка входа');
        }
    };

    // Выход пользователя
    const handleLogout = () => {
        localStorage.removeItem('username'); // Удаляем данные из localStorage
        localStorage.removeItem('password');
        setIsLoggedIn(false);
        setUsername('');
        setPassword('');
        setCurrentFolder('');
        setFolders([]);
        setFiles([]);
    };

    // Загрузка списка папок и файлов
    const loadFolders = async () => {
        setLoading(true);
        try {
            const response = await axios.get('http://26.105.9.189:5000/files', { params: { username, folderName: currentFolder } });
            setFolders(response.data.folders);
            setFiles(response.data.files);
        } catch (error) {
            setMessage('Ошибка при загрузке данных');
        } finally {
            setLoading(false);
        }
    };

    // Создание новой папки
    const handleCreateFolder = async () => {
        const folderName = prompt('Введите имя новой папки');
        if (!folderName) return;
        try {
            const response = await axios.post('http://26.105.9.189:5000/createFolder', { username, folderName, parentFolder: currentFolder });
            setMessage(response.data.message);
            loadFolders();
        } catch (error) {
            setMessage(error.response?.data?.message || 'Ошибка при создании папки');
        }
    };

    // Загрузка файлов в текущую папку
    const handleFileUpload = async (event) => {
        const formData = new FormData();
        for (let i = 0; i < event.target.files.length; i++) {
            formData.append('files', event.target.files[i]);
        }
        setLoading(true);
        try {
            const response = await axios.post('http://26.105.9.189:5000/upload', formData, {
                params: { username, folderName: currentFolder }
            });
            setMessage(response.data.message);
            loadFolders(); // Обновляем папки и файлы
        } catch (error) {
            setMessage('Ошибка при загрузке файлов');
        } finally {
            setLoading(false);
        }
    };

    // Переход в подкаталог
    const handleFolderClick = (folderName) => {
        setCurrentFolder(prev => (prev ? `${prev}/${folderName}` : folderName));
        loadFolders();
    };

    // Переход назад
    const handleGoBack = () => {
        setCurrentFolder(prev => prev.split('/').slice(0, -1).join('/'));
        loadFolders();
    };

    // Скачивание файла
    const handleDownloadFile = (fileName) => {
        const url = `http://26.105.9.189:5000/download?username=${username}&fileName=${fileName}&folderName=${currentFolder}`;
        window.open(url, '_blank');
    };

    // Удаление файла
    const handleDeleteFile = async (fileName) => {
        if (window.confirm(`Вы уверены, что хотите удалить файл ${fileName}?`)) {
            try {
                const response = await axios.delete('http://26.105.9.189:5000/deleteFile', {
                    data: { username, fileName, folderName: currentFolder }
                });
                setMessage(response.data.message);
                loadFolders();
            } catch (error) {
                setMessage('Ошибка при удалении файла');
            }
        }
    };

    // Удаление папки
    const handleDeleteFolder = async (folderName) => {
        if (window.confirm(`Вы уверены, что хотите удалить папку ${folderName}?`)) {
            try {
                const response = await axios.delete('http://26.105.9.189:5000/deleteFolder', {
                    data: { username, folderName, parentFolder: currentFolder }
                });
                setMessage(response.data.message);
                loadFolders();
            } catch (error) {
                setMessage('Ошибка при удалении папки');
            }
        }
    };

    return (
        <div>
            <h1>Файловая система</h1>

            {/* Регистрация / Вход */}
            {!isLoggedIn && !isRegistering && (
                <div>
                    <h2>Вход</h2>
                    <input
                        type="text"
                        placeholder="Имя пользователя"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Пароль"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button onClick={handleLogin}>Войти</button>
                    <button onClick={() => setIsRegistering(true)}>Зарегистрироваться</button>
                </div>
            )}

            {/* Регистрация */}
            {isRegistering && (
                <div>
                    <h2>Регистрация</h2>
                    <input
                        type="text"
                        placeholder="Имя пользователя"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Пароль"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button onClick={handleRegister}>Зарегистрироваться</button>
                </div>
            )}

            {/* Главная страница после входа */}
            {isLoggedIn && (
                <div>
                    <h2>Добро пожаловать, {username}</h2>
                    <button onClick={handleLogout}>Выйти</button>

                    {/* Навигация по папкам */}
                    <div>
                        <h3>Текущий путь: {currentFolder ? `${currentFolder}/` : 'Главная папка'}</h3>
                        {currentFolder && <button onClick={handleGoBack}>Перейти назад</button>}
                    </div>

                    {/* Список папок */}
                    <div>
                        <h3>Папки</h3>
                        {loading ? (
                            <p>Загрузка...</p>
                        ) : (
                            <div>
                                {folders.length === 0 ? (
                                    <p>Нет папок</p>
                                ) : (
                                    folders.map((folder) => (
                                        <div key={folder}>
                                            <span
                                                onClick={() => handleFolderClick(folder)}
                                                style={{ cursor: 'pointer', marginRight: '10px' }}
                                            >
                                                {folder}
                                            </span>
                                            <button onClick={() => handleDeleteFolder(folder)}>Удалить папку</button>
                                            <button onClick={() => handleFolderClick(folder)}>Перейти в папку</button>
                                        </div>
                                    ))
                                )}
                            </div>
                        )}
                        <button onClick={handleCreateFolder}>Создать новую папку</button>
                    </div>

                    {/* Загрузка файлов */}
                    <div>
                        <h3>Загрузить файлы</h3>
                        <input type="file" multiple onChange={handleFileUpload} />
                    </div>

                    {/* Список файлов */}
                    <div>
                        <h3>Файлы</h3>
                        {files.length === 0 ? (
                            <p>Нет файлов в этой папке</p>
                        ) : (
                            files.map((file) => (
                                <div key={file}>
                                    <span>{file}</span>
                                    <button onClick={() => handleDownloadFile(file)}>Скачать</button>
                                    <button onClick={() => handleDeleteFile(file)}>Удалить</button>
                                </div>
                            ))
                        )}
                    </div>
                </div>
            )}

            <p>{message}</p>
        </div>
    );
};

export default App;
