import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isRegistering, setIsRegistering] = useState(false);
    const [folders, setFolders] = useState([]);
    const [files, setFiles] = useState([]);
    const [currentFolder, setCurrentFolder] = useState('');
    const [previousFolder, setPreviousFolder] = useState('');

    const handleRegister = async () => {
        try {
            const response = await axios.post('http://localhost:5000/register', { username, password });
            setMessage(response?.data?.message || 'Успех');
            setIsRegistering(false);
        } catch (error) {
            setMessage(error?.response?.data?.message || 'Ошибка при регистрации');
        }
    };

    const handleLogin = async () => {
        try {
            const response = await axios.post('http://localhost:5000/login', { username, password });
            setMessage(response?.data?.message || 'Успех');
            setIsLoggedIn(true);
            loadFoldersAndFiles();
        } catch (error) {
            setMessage(error?.response?.data?.message || 'Ошибка при входе');
        }
    };

    const loadFoldersAndFiles = async () => {
        try {
            const response = await axios.get('http://localhost:5000/files', {
                params: { username, currentFolder }
            });
            setFolders(response?.data?.folders || []);
            setFiles(response?.data?.files || []);
        } catch (error) {
            setMessage('Ошибка при загрузке папок или файлов');
        }
    };

    const refreshFoldersAndFiles = () => {
        loadFoldersAndFiles();
    };

    const handleDownloadFile = async (fileName) => {
        try {
            const response = await axios.get('http://localhost:5000/download', {
                params: { username, fileName, currentFolder },
                responseType: 'blob',
            });
            const link = document.createElement('a');
            link.href = URL.createObjectURL(response.data);
            link.download = fileName;
            link.click();
        } catch (error) {
            setMessage('Ошибка при скачивании файла');
        }
    };

    const handleEnterFolder = (folderName) => {
        setPreviousFolder(currentFolder);
        setCurrentFolder(folderName);
        loadFoldersAndFiles();
    };

    const handleBack = () => {
        setCurrentFolder(previousFolder);
        loadFoldersAndFiles();
    };

    const handleFileUpload = async (event) => {
        const formData = new FormData();
        Array.from(event.target.files).forEach(file => formData.append('files', file));

        try {
            await axios.post('http://localhost:5000/upload', formData, {
                params: { username, currentFolder }
            });
            loadFoldersAndFiles();
        } catch (error) {
            setMessage('Ошибка при загрузке файлов');
        }
    };

    const handleCreateFolder = async () => {
        const folderName = prompt('Введите имя новой папки');
        if (folderName) {
            try {
                await axios.post('http://localhost:5000/createFolder', {
                    username, folderName, currentFolder
                });
                loadFoldersAndFiles();
            } catch (error) {
                setMessage('Ошибка при создании папки');
            }
        }
    };

    const handleDeleteFile = async (fileName) => {
        try {
            await axios.delete('http://localhost:5000/deleteFile', {
                data: { username, fileName, currentFolder }
            });
            loadFoldersAndFiles();
        } catch (error) {
            setMessage('Ошибка при удалении файла');
        }
    };

    const handleDeleteFolder = async (folderName) => {
        try {
            await axios.delete('http://localhost:5000/deleteFolder', {
                data: { username, folderName, currentFolder }
            });
            loadFoldersAndFiles();
        } catch (error) {
            setMessage('Ошибка при удалении папки');
        }
    };

    useEffect(() => {
        if (isLoggedIn) {
            loadFoldersAndFiles();
        }
    }, [currentFolder, isLoggedIn]);

    return (
        <div className="container">
            <h2 className="title">Файловая система</h2>

            {!isLoggedIn && !isRegistering && (
                <div className="auth-section">
                    <h3>Вход</h3>
                    <input type="text" placeholder="Имя пользователя" value={username} onChange={(e) => setUsername(e.target.value)} />
                    <input type="password" placeholder="Пароль" value={password} onChange={(e) => setPassword(e.target.value)} />
                    <button className="button" onClick={handleLogin}>Войти</button>
                    <button className="button" onClick={() => setIsRegistering(true)}>Зарегистрироваться</button>
                </div>
            )}

            {isRegistering && (
                <div className="auth-section">
                    <h3>Регистрация</h3>
                    <input type="text" placeholder="Имя пользователя" value={username} onChange={(e) => setUsername(e.target.value)} />
                    <input type="password" placeholder="Пароль" value={password} onChange={(e) => setPassword(e.target.value)} />
                    <button className="button" onClick={handleRegister}>Зарегистрироваться</button>
                </div>
            )}

            {isLoggedIn && (
                <div className="dashboard">
                    <div className="path">
                        {currentFolder && (
                            <button className="button" onClick={handleBack}>Перейти назад</button>
                        )}
                    </div>

                    <h3>Список папок</h3>
                    <div className="file-folder-list">
                        <div className="file-folder-items">
                            {folders.map((folder) => (
                                <div key={folder} className="file-folder-item">
                                    <span>{folder}</span>
                                    <button className="button" onClick={() => handleEnterFolder(folder)}>Перейти в папку</button>
                                    <button className="button" onClick={() => handleDeleteFolder(folder)}>Удалить папку</button>
                                </div>
                            ))}
                        </div>

                        <div className="action-buttons">
                            <button className="button" onClick={handleCreateFolder}>Создать папку</button>
                            <button className="button" onClick={refreshFoldersAndFiles}>Обновить список</button>
                        </div>
                    </div>

                    <h3>Список файлов</h3>
                    <div className="file-folder-list">
                        <div className="file-folder-items">
                            {files.map((file) => (
                                <div key={file} className="file-folder-item">
                                    <span>{file}</span>
                                    <button className="button" onClick={() => handleDownloadFile(file)}>Скачать файл</button>
                                    <button className="button" onClick={() => handleDeleteFile(file)}>Удалить файл</button>
                                </div>
                            ))}
                        </div>

                        <div className="upload-section">
                            <input className="file-input" type="file" multiple onChange={handleFileUpload} />
                        </div>
                    </div>
                </div>
            )}

            <p>{message}</p>
        </div>
    );
};

export default App;
