import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isRegistering, setIsRegistering] = useState(false);
    const [folders, setFolders] = useState([]);
    const [currentFolder, setCurrentFolder] = useState('');
    const [fileInput, setFileInput] = useState(null);

    // Регистрация нового пользователя
    const handleRegister = async () => {
        try {
            const response = await axios.post('http://26.105.9.189:5000/register', { username, password });
            setMessage(response.data.message);
            setIsRegistering(false);
        } catch (error) {
            setMessage(error.response.data.message);
        }
    };

    // Вход пользователя
    const handleLogin = async () => {
        try {
            const response = await axios.post('http://26.105.9.189:5000/login', { username, password });
            setMessage(response.data.message);
            setIsLoggedIn(true);
            loadFolders();
        } catch (error) {
            setMessage(error.response.data.message);
        }
    };

    // Загрузка папок
    const loadFolders = async () => {
        try {
            const response = await axios.get('http://26.105.9.189:5000/files', {
                params: { username, currentFolder }
            });
            setFolders(response.data.files);
        } catch (error) {
            setMessage('Ошибка при загрузке папок');
        }
    };

    // Создание папки
    const handleCreateFolder = async () => {
        const folderName = prompt('Введите имя новой папки');
        if (!folderName) {
            return;
        }
        try {
            const response = await axios.post('http://26.105.9.189:5000/createFolder', {
                username,
                folderName,
                parentFolder: currentFolder
            });
            setMessage(response.data.message);
            loadFolders();
        } catch (error) {
            setMessage(error.response.data.message);
        }
    };

    // Загрузка файлов
    const handleFileUpload = async (event) => {
        const formData = new FormData();
        for (let i = 0; i < event.target.files.length; i++) {
            formData.append('files', event.target.files[i]);
        }
        try {
            const response = await axios.post('http://26.105.9.189:5000/upload', formData, {
                params: { username, currentFolder }
            });
            setMessage(response.data.message);
            loadFolders(); // Обновляем список папок после загрузки
        } catch (error) {
            setMessage('Ошибка при загрузке файлов');
        }
    };

    // Переход в папку
    const handleEnterFolder = (folderName) => {
        setCurrentFolder(folderName);
        loadFolders(); // Загружаем файлы внутри папки
    };

    // Переход назад
    const handleGoBack = () => {
        const parentFolder = currentFolder.split('/').slice(0, -1).join('/');
        setCurrentFolder(parentFolder);
        loadFolders(); // Загружаем файлы в родительской папке
    };

    // Удаление папки
    const handleDeleteFolder = async (folderName) => {
        try {
            const response = await axios.delete('http://26.105.9.189:5000/deleteFolder', {
                data: { username, folderName, parentFolder: currentFolder }
            });
            setMessage(response.data.message);
            loadFolders(); // Обновляем список папок после удаления
        } catch (error) {
            setMessage('Ошибка при удалении папки');
        }
    };

    useEffect(() => {
        if (isLoggedIn) {
            loadFolders(); // Загружаем папки при входе
        }
    }, [currentFolder, isLoggedIn]);

    return (
        <div>
            <h1>Файловая система</h1>

            {/* Регистрация / Вход */}
            {!isLoggedIn && !isRegistering && (
                <div>
                    <h2>Вход</h2>
                    <input
                        type="text"
                        placeholder="Имя пользователя"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Пароль"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button onClick={handleLogin}>Войти</button>
                    <button onClick={() => setIsRegistering(true)}>Зарегистрироваться</button>
                </div>
            )}

            {/* Регистрация */}
            {isRegistering && (
                <div>
                    <h2>Регистрация</h2>
                    <input
                        type="text"
                        placeholder="Имя пользователя"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Пароль"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button onClick={handleRegister}>Зарегистрироваться</button>
                </div>
            )}

            {/* Главная страница после входа */}
            {isLoggedIn && (
                <div>
                    <h2>Добро пожаловать, {username}</h2>
                    <button onClick={() => setIsLoggedIn(false)}>Выйти</button>

                    {/* Путь к папке */}
                    <div>
                        <span>Путь: {currentFolder || 'Главная'}</span>
                        {currentFolder && (
                            <button onClick={handleGoBack}>Перейти назад</button>
                        )}
                    </div>

                    {/* Список папок */}
                    <div>
                        <h3>Папки</h3>
                        {folders.length === 0 ? (
                            <p>Нет папок</p>
                        ) : (
                            folders.map((folder) => (
                                <div key={folder}>
                                    <span>{folder}</span>
                                    <button onClick={() => handleEnterFolder(folder)}>Перейти в папку</button>
                                    <button onClick={() => handleDeleteFolder(folder)}>Удалить папку</button>
                                </div>
                            ))
                        )}

                        <button onClick={handleCreateFolder}>Создать новую папку</button>
                    </div>

                    {/* Загрузка файлов */}
                    <div>
                        <h3>Загрузить файлы</h3>
                        <input type="file" multiple onChange={handleFileUpload} />
                    </div>
                </div>
            )}

            <p>{message}</p>
        </div>
    );
};

export default App;
