import React, { useState, useEffect } from 'react';

function FileUpload() {
    const [selectedFiles, setSelectedFiles] = useState(null);
    const [folderName, setFolderName] = useState('');
    const [responseMessage, setResponseMessage] = useState('');
    const [fileList, setFileList] = useState([]); // Для списка файлов
    const [showFileList, setShowFileList] = useState(false); // Флаг для отображения списка файлов

    const handleFileChange = (event) => {
        setSelectedFiles(event.target.files);
    };

    const handleFolderChange = (event) => {
        setFolderName(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        if (!selectedFiles || !folderName) {
            setResponseMessage('Пожалуйста, укажите имя папки и выберите файлы для загрузки.');
            return;
        }

        const formData = new FormData();
        formData.append('folderName', folderName);

        // Добавляем файлы в форму
        for (let i = 0; i < selectedFiles.length; i++) {
            formData.append('files', selectedFiles[i]);
        }

        try {
            const response = await fetch('http://26.105.9.189:5000/upload', {
                method: 'POST',
                body: formData
            });

            if (response.ok) {
                const data = await response.json();
                setResponseMessage(data.message);
                fetchFileList(folderName); // После загрузки файлов запрашиваем список
            } else {
                setResponseMessage('Ошибка при загрузке файлов.');
            }
        } catch (error) {
            console.error('Ошибка:', error);
            setResponseMessage('Ошибка при соединении с сервером.');
        }
    };

    // Функция для получения списка файлов
    const fetchFileList = async (folderName) => {
        try {
            const response = await fetch(`http://26.105.9.189:5000/files/${folderName}`);
            if (response.ok) {
                const data = await response.json();
                setFileList(data.files);
                setShowFileList(true); // Показываем список файлов
            } else {
                setResponseMessage('Ошибка при получении списка файлов.');
            }
        } catch (error) {
            console.error('Ошибка:', error);
            setResponseMessage('Ошибка при соединении с сервером.');
        }
    };

    // Функция для удаления файла
    const deleteFile = async (fileName) => {
        try {
            const response = await fetch(`http://26.105.9.189:5000/files/${folderName}/${fileName}`, {
                method: 'DELETE'
            });

            if (response.ok) {
                const data = await response.json();
                setResponseMessage(data.message);
                fetchFileList(folderName); // После удаления файла обновляем список
            } else {
                setResponseMessage('Ошибка при удалении файла.');
            }
        } catch (error) {
            console.error('Ошибка:', error);
            setResponseMessage('Ошибка при соединении с сервером.');
        }
    };

    return (
        <div>
            <h1>Загрузка файлов</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="folderName">Имя папки:</label>
                    <input
                        type="text"
                        id="folderName"
                        value={folderName}
                        onChange={handleFolderChange}
                        placeholder="Введите имя папки"
                    />
                </div>

                <div>
                    <input type="file" multiple onChange={handleFileChange} />
                </div>

                <div>
                    <button type="submit">Загрузить файлы</button>
                </div>
            </form>

            {responseMessage && <p>{responseMessage}</p>}

            {/* Раздел для просмотра и удаления файлов */}
            {showFileList && (
                <div style={{ marginTop: '20px' }}>
                    <h2>Файлы в папке "{folderName}":</h2>
                    <ul>
                        {fileList.length > 0 ? (
                            fileList.map((file, index) => (
                                <li key={index}>
                                    {file}
                                    <button onClick={() => deleteFile(file)} style={{ marginLeft: '10px' }}>
                                        Удалить
                                    </button>
                                </li>
                            ))
                        ) : (
                            <li>Нет файлов в этой папке.</li>
                        )}
                    </ul>
                </div>
            )}
        </div>
    );
}

export default FileUpload;
