import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isRegistering, setIsRegistering] = useState(false);
    const [files, setFiles] = useState([]);
    const [folders, setFolders] = useState([]);
    const [currentFolder, setCurrentFolder] = useState('');

    // Регистрация нового пользователя
    const handleRegister = async () => {
        try {
            const response = await axios.post('http://localhost:5000/register', { username, password });
            setMessage(response.data.message);
            setIsRegistering(false);
        } catch (error) {
            setMessage(error.response.data.message);
        }
    };

    // Вход пользователя
    const handleLogin = async () => {
        try {
            const response = await axios.post('http://localhost:5000/login', { username, password });
            setMessage(response.data.message);
            setIsLoggedIn(true);
            loadFiles();
            loadFolders();
        } catch (error) {
            setMessage(error.response.data.message);
        }
    };

    // Загрузка файлов
    const loadFiles = async () => {
        try {
            const response = await axios.get('http://localhost:5000/files');
            setFiles(response.data.files);
        } catch (error) {
            setMessage('Ошибка при загрузке файлов');
        }
    };

    // Загрузка папок
    const loadFolders = async () => {
        try {
            const response = await axios.get('http://localhost:5000/folders');
            setFolders(response.data.folders);
        } catch (error) {
            setMessage('Ошибка при загрузке папок');
        }
    };

    // Создание папки
    const handleCreateFolder = async (folderName) => {
        try {
            const response = await axios.post('http://localhost:5000/folders', { folderName });
            setMessage(response.data.message);
            loadFolders();
        } catch (error) {
            setMessage(error.response.data.message);
        }
    };

    // Удаление файла
    const handleDeleteFile = async (fileName) => {
        try {
            const response = await axios.delete(`http://localhost:5000/files/${fileName}`);
            setMessage(response.data.message);
            loadFiles();
        } catch (error) {
            setMessage(error.response.data.message);
        }
    };

    // Загрузка файлов
    const handleFileUpload = async (event) => {
        const formData = new FormData();
        for (let file of event.target.files) {
            formData.append('files', file);
        }
        try {
            const response = await axios.post('http://localhost:5000/upload', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });
            setMessage(response.data.message);
            loadFiles();
        } catch (error) {
            setMessage('Ошибка при загрузке файлов');
        }
    };

    return (
        <div>
            <h1>Файловый менеджер</h1>

            {!isLoggedIn && !isRegistering && (
                <div>
                    <button onClick={() => setIsRegistering(true)}>Зарегистрироваться</button>
                    <button onClick={() => setIsRegistering(false)}>Войти</button>
                </div>
            )}

            {isRegistering && (
                <div>
                    <h2>Регистрация</h2>
                    <input
                        type="text"
                        placeholder="Имя пользователя"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Пароль"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button onClick={handleRegister}>Зарегистрироваться</button>
                </div>
            )}

            {!isRegistering && !isLoggedIn && (
                <div>
                    <h2>Вход</h2>
                    <input
                        type="text"
                        placeholder="Имя пользователя"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Пароль"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button onClick={handleLogin}>Войти</button>
                </div>
            )}

            {isLoggedIn && (
                <div>
                    <h2>Добро пожаловать, {username}</h2>

                    {/* Список файлов */}
                    <div>
                        <h3>Файлы</h3>
                        {files.length === 0 ? (
                            <p>Нет файлов</p>
                        ) : (
                            files.map((file) => (
                                <div key={file}>
                                    <span>{file}</span>
                                    <button onClick={() => handleDeleteFile(file)}>Удалить</button>
                                </div>
                            ))
                        )}

                        <input type="file" multiple onChange={handleFileUpload} />
                    </div>

                    {/* Список папок */}
                    <div>
                        <h3>Папки</h3>
                        {folders.length === 0 ? (
                            <p>Нет папок</p>
                        ) : (
                            folders.map((folder) => (
                                <div key={folder}>
                                    <span>{folder}</span>
                                    {/* Навигация по папкам */}
                                    <button onClick={() => setCurrentFolder(folder)}>Перейти</button>
                                    <button onClick={() => handleDeleteFolder(folder)}>Удалить</button>
                                </div>
                            ))
                        )}

                        <button onClick={() => handleCreateFolder(prompt('Введите имя новой папки'))}>Создать новую папку</button>
                    </div>

                    {/* Кнопка выхода */}
                    <button onClick={() => setIsLoggedIn(false)}>Выйти</button>
                </div>
            )}

            <p>{message}</p>
        </div>
    );
};

export default App;
