import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isRegistering, setIsRegistering] = useState(false);
    const [folders, setFolders] = useState([]);
    const [files, setFiles] = useState([]);
    const [currentFolder, setCurrentFolder] = useState('');

    const handleRegister = async () => {
        try {
            const response = await axios.post('http://26.105.9.189:5000/register', { username, password });
            setMessage(response?.data?.message || 'Успех');
            setIsRegistering(false);
        } catch (error) {
            console.error(error);
            setMessage(error?.response?.data?.message || 'Ошибка при регистрации');
        }
    };

    const handleLogin = async () => {
        try {
            const response = await axios.post('http://26.105.9.189:5000/login', { username, password });
            setMessage(response?.data?.message || 'Успех');
            setIsLoggedIn(true);
            loadFoldersAndFiles();
        } catch (error) {
            console.error(error);
            setMessage(error?.response?.data?.message || 'Ошибка при входе');
        }
    };

    const loadFoldersAndFiles = async () => {
        try {
            const response = await axios.get('http://26.105.9.189:5000/files', {
                params: { username, currentFolder }
            });
            setFolders(response?.data?.folders || []);
            setFiles(response?.data?.files || []);
        } catch (error) {
            console.error(error);
            setMessage('Ошибка при загрузке папок и файлов');
        }
    };

    const handleCreateFolder = async () => {
        const folderName = prompt('Введите имя новой папки');
        if (!folderName) {
            return;
        }
        try {
            const response = await axios.post('http://26.105.9.189:5000/createFolder', {
                username,
                folderName,
                parentFolder: currentFolder
            });
            setMessage(response?.data?.message || 'Папка успешно создана');
            loadFoldersAndFiles();
        } catch (error) {
            console.error(error);
            setMessage(error?.response?.data?.message || 'Ошибка при создании папки');
        }
    };

    const handleFileUpload = async (event) => {
        const formData = new FormData();
        for (let i = 0; i < event.target.files.length; i++) {
            formData.append('files', event.target.files[i]);
        }
        try {
            const response = await axios.post('http://26.105.9.189:5000/upload', formData, {
                params: { username, currentFolder }
            });
            setMessage(response?.data?.message || 'Файлы успешно загружены');
            loadFoldersAndFiles();
        } catch (error) {
            console.error(error);
            setMessage('Ошибка при загрузке файлов');
        }
    };

    const handleDelete = async (item, type) => {
        try {
            const response = await axios.delete(`http://26.105.9.189:5000/${type}`, {
                data: { username, name: item, parentFolder: currentFolder }
            });
            setMessage(response?.data?.message || `${type === 'folder' ? 'Папка' : 'Файл'} успешно удален`);
            loadFoldersAndFiles();
        } catch (error) {
            console.error(error);
            setMessage(`Ошибка при удалении ${type === 'folder' ? 'папки' : 'файла'}`);
        }
    };

    return (
        <div className="container">
            <h1>Файловая система</h1>

            {!isLoggedIn && !isRegistering && (
                <div>
                    <h2>Вход</h2>
                    <input
                        type="text"
                        placeholder="Имя пользователя"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Пароль"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button onClick={handleLogin}>Войти</button>
                    <button onClick={() => setIsRegistering(true)}>Зарегистрироваться</button>
                </div>
            )}

            {isRegistering && (
                <div>
                    <h2>Регистрация</h2>
                    <input
                        type="text"
                        placeholder="Имя пользователя"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Пароль"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button onClick={handleRegister}>Зарегистрироваться</button>
                </div>
            )}

            {isLoggedIn && (
                <div>
                    <button onClick={() => setIsLoggedIn(false)}>Выйти</button>
                    <h2>Добро пожаловать, {username}</h2>
                    <div>
                        {currentFolder && (
                            <button onClick={() => setCurrentFolder('')}>Назад</button>
                        )}
                        <span>{currentFolder || 'Главная папка'}</span>
                    </div>
                    <div className="files-container">
                        {folders.map((folder) => (
                            <div className="file-item" key={folder}>
                                <span>{folder}</span>
                                <button onClick={() => handleDelete(folder, 'folder')}>Удалить</button>
                            </div>
                        ))}
                        {files.map((file) => (
                            <div className="file-item" key={file}>
                                <span>{file}</span>
                                <button onClick={() => handleDelete(file, 'file')}>Удалить</button>
                            </div>
                        ))}
                    </div>
                    <input type="file" multiple onChange={handleFileUpload} />
                    <button onClick={handleCreateFolder}>Создать папку</button>
                </div>
            )}

            <p>{message}</p>
        </div>
    );
};

export default App;
