import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isRegistering, setIsRegistering] = useState(false);
    const [folders, setFolders] = useState([]);
    const [files, setFiles] = useState([]);
    const [currentFolder, setCurrentFolder] = useState('');
    const [loading, setLoading] = useState(false);

    // Регистрация нового пользователя
    const handleRegister = async () => {
        try {
            const response = await axios.post('http://26.105.9.189:5000/register', { username, password });
            setMessage(response.data.message);
            setIsRegistering(false);
        } catch (error) {
            setMessage(error.response?.data?.message || 'Ошибка при регистрации');
        }
    };

    // Вход пользователя
    const handleLogin = async () => {
        try {
            const response = await axios.post('http://26.105.9.189:5000/login', { username, password });
            setMessage(response.data.message);
            setIsLoggedIn(true);
            setCurrentFolder(''); // Начать с основной папки
            loadFolders('');
        } catch (error) {
            setMessage(error.response?.data?.message || 'Ошибка при входе');
        }
    };

    // Загрузка папок и файлов
    const loadFolders = async (folder) => {
        try {
            setLoading(true);
            const response = await axios.get('http://26.105.9.189:5000/files', {
                params: { username, currentFolder: folder }
            });

            setFolders(response.data.folders);
            setFiles(response.data.files);
            setLoading(false);
        } catch (error) {
            setLoading(false);
            setMessage('Ошибка при загрузке данных');
        }
    };

    // Загрузка файлов
    const handleFileUpload = async (event) => {
        const formData = new FormData();
        for (let i = 0; i < event.target.files.length; i++) {
            formData.append('files', event.target.files[i]);
        }

        try {
            setLoading(true);
            await axios.post('http://26.105.9.189:5000/upload', formData, {
                params: { username, currentFolder }
            });
            setMessage('Файлы успешно загружены');
            loadFolders(currentFolder);
        } catch (error) {
            setMessage('Ошибка при загрузке файлов');
            setLoading(false);
        }
    };

    // Скачивание файла
    const handleDownloadFile = (fileName) => {
        window.location.href = `http://26.105.9.189:5000/download?username=${username}&fileName=${fileName}&currentFolder=${currentFolder}`;
    };

    // Удаление файла
    const handleDeleteFile = async (fileName) => {
        try {
            await axios.delete('http://26.105.9.189:5000/deleteFile', {
                data: { username, fileName, currentFolder }
            });
            setMessage('Файл удален');
            loadFolders(currentFolder);
        } catch (error) {
            setMessage('Ошибка при удалении файла');
        }
    };

    // Удаление папки
    const handleDeleteFolder = async (folderName) => {
        try {
            await axios.delete('http://26.105.9.189:5000/deleteFolder', {
                data: { username, folderName, currentFolder }
            });
            setMessage('Папка удалена');
            loadFolders(currentFolder);
        } catch (error) {
            setMessage('Ошибка при удалении папки');
        }
    };

    // Создание новой папки
    const handleCreateFolder = async () => {
        const folderName = prompt('Введите имя новой папки');
        if (!folderName) return;

        try {
            await axios.post('http://26.105.9.189:5000/createFolder', {
                username,
                folderName,
                currentFolder
            });
            setMessage('Папка успешно создана');
            loadFolders(currentFolder);
        } catch (error) {
            setMessage('Ошибка при создании папки');
        }
    };

    // Переход в папку
    const handleGoToFolder = (folderName) => {
        setCurrentFolder(folderName);
        loadFolders(folderName);
    };

    // Переход назад
    const handleGoBack = () => {
        if (currentFolder) {
            const parentFolder = currentFolder.split('/').slice(0, -1).join('/');
            setCurrentFolder(parentFolder);
            loadFolders(parentFolder);
        }
    };

    // Компонент загрузки файлов
    const renderFileUpload = () => (
        <div>
            <input type="file" multiple onChange={handleFileUpload} />
        </div>
    );

    // Компонент списка файлов и папок
    const renderFileAndFolderList = () => (
        <div>
            <h3>Папки:</h3>
            {folders.length === 0 && <p>Нет папок</p>}
            {folders.map((folder) => (
                <div key={folder}>
                    <span>{folder}</span>
                    <button onClick={() => handleGoToFolder(folder)}>Перейти в папку</button>
                    <button onClick={() => handleDeleteFolder(folder)}>Удалить</button>
                </div>
            ))}
            <h3>Файлы:</h3>
            {files.length === 0 && <p>Нет файлов</p>}
            {files.map((file) => (
                <div key={file}>
                    <span>{file}</span>
                    <button onClick={() => handleDownloadFile(file)}>Скачать</button>
                    <button onClick={() => handleDeleteFile(file)}>Удалить</button>
                </div>
            ))}
        </div>
    );

    // Регистрация / Вход
    const renderAuthForm = () => (
        <div>
            <h2>Вход</h2>
            <input
                type="text"
                placeholder="Имя пользователя"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
            />
            <input
                type="password"
                placeholder="Пароль"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleLogin}>Войти</button>
            <button onClick={() => setIsRegistering(true)}>Зарегистрироваться</button>
        </div>
    );

    // Регистрация
    const renderRegisterForm = () => (
        <div>
            <h2>Регистрация</h2>
            <input
                type="text"
                placeholder="Имя пользователя"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
            />
            <input
                type="password"
                placeholder="Пароль"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            />
            <button onClick={handleRegister}>Зарегистрироваться</button>
            <button onClick={() => setIsRegistering(false)}>Отмена</button>
        </div>
    );

    return (
        <div style={{ padding: '20px' }}>
            <h1>Файловая система</h1>
            {message && <p>{message}</p>}

            {/* Регистрация / Вход */}
            {!isLoggedIn && !isRegistering && renderAuthForm()}

            {/* Регистрация */}
            {isRegistering && renderRegisterForm()}

            {/* Главная страница после входа */}
            {isLoggedIn && (
                <div>
                    <h2>Добро пожаловать, {username}</h2>
                    <button onClick={() => setIsLoggedIn(false)}>Выйти</button>

                    {/* Путь к папке и кнопка назад */}
                    {currentFolder && (
                        <div>
                            <p>
                                Путь: {currentFolder}
                                <button onClick={handleGoBack}>Перейти назад</button>
                            </p>
                        </div>
                    )}

                    {/* Список папок и файлов */}
                    {renderFileAndFolderList()}

                    {/* Кнопка создания папки */}
                    <button onClick={handleCreateFolder}>Создать папку</button>

                    {/* Кнопка обновления списка */}
                    <button onClick={() => loadFolders(currentFolder)}>Обновить список</button>

                    {/* Загрузка файлов */}
                    {renderFileUpload()}
                </div>
            )}
        </div>
    );
};

export default App;
