import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isRegistering, setIsRegistering] = useState(false);
    const [folders, setFolders] = useState([]);
    const [currentFolder, setCurrentFolder] = useState('');

    // Регистрация нового пользователя
    const handleRegister = async () => {
        try {
            const response = await axios.post('http://26.105.9.189:5000/register', { username, password });
            setMessage(response?.data?.message || 'Успех');
            setIsRegistering(false);
        } catch (error) {
            setMessage(error?.response?.data?.message || 'Ошибка при регистрации');
        }
    };

    // Вход пользователя
    const handleLogin = async () => {
        try {
            const response = await axios.post('http://26.105.9.189:5000/login', { username, password });
            setMessage(response?.data?.message || 'Успех');
            setIsLoggedIn(true);
            loadFolders();
        } catch (error) {
            setMessage(error?.response?.data?.message || 'Ошибка при входе');
        }
    };

    // Загрузка списка папок
    const loadFolders = async () => {
        try {
            const response = await axios.get('http://26.105.9.189:5000/files', {
                params: { username, currentFolder }
            });
            setFolders(response?.data?.files || []);
        } catch (error) {
            setMessage('Ошибка при загрузке папок');
        }
    };

    // Обновление списка папок
    const refreshFolders = () => {
        loadFolders();
    };

    // Загрузка файла
    const handleDownloadFile = async (fileName) => {
        try {
            const response = await axios.get('http://26.105.9.189:5000/download', {
                params: { username, fileName, currentFolder },
                responseType: 'blob',
            });
            const link = document.createElement('a');
            link.href = URL.createObjectURL(response.data);
            link.download = fileName;
            link.click();
        } catch (error) {
            setMessage('Ошибка при скачивании файла');
        }
    };

    // Переход в папку
    const handleEnterFolder = (folderName) => {
        setCurrentFolder(folderName);
        loadFolders();
    };

    // Переход назад
    const handleBack = () => {
        setCurrentFolder('');
        loadFolders();
    };

    return (
        <div>
            <h1>Файловая система</h1>

            {/* Регистрация / Вход */}
            {!isLoggedIn && !isRegistering && (
                <div>
                    <h2>Вход</h2>
                    <input
                        type="text"
                        placeholder="Имя пользователя"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Пароль"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button onClick={handleLogin}>Войти</button>
                    <button onClick={() => setIsRegistering(true)}>Зарегистрироваться</button>
                </div>
            )}

            {/* Регистрация */}
            {isRegistering && (
                <div>
                    <h2>Регистрация</h2>
                    <input
                        type="text"
                        placeholder="Имя пользователя"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        type="password"
                        placeholder="Пароль"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button onClick={handleRegister}>Зарегистрироваться</button>
                </div>
            )}

            {/* Главная страница после входа */}
            {isLoggedIn && (
                <div>
                    <h2>Добро пожаловать, {username}</h2>
                    <button onClick={() => setIsLoggedIn(false)}>Выйти</button>

                    {/* Путь */}
                    <div>
                        <button onClick={handleBack}>Перейти назад</button>
                        <span>{currentFolder ? `Текущая папка: ${currentFolder}` : 'Главная папка'}</span>
                    </div>

                    {/* Список папок */}
                    <div>
                        <h3>Папки</h3>
                        {folders.length === 0 ? (
                            <p>Нет папок</p>
                        ) : (
                            folders.map((folder) => (
                                <div key={folder}>
                                    <span>{folder}</span>
                                    <button onClick={() => handleEnterFolder(folder)}>Перейти в папку</button>
                                    <button onClick={() => handleDownloadFile(folder)}>Скачать файл</button>
                                </div>
                            ))
                        )}
                        <button onClick={refreshFolders}>Обновить список папок</button>
                    </div>

                    {/* Загрузка файлов */}
                    <div>
                        <h3>Загрузить файлы</h3>
                        <input type="file" multiple onChange={handleFileUpload} />
                    </div>
                </div>
            )}

            <p>{message}</p>
        </div>
    );
};

export default App;
