import React, { useState, useEffect } from 'react';
import axios from 'axios';


const App = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isRegistering, setIsRegistering] = useState(false);
    const [folders, setFolders] = useState([]);
    const [files, setFiles] = useState([]);
    const [currentFolder, setCurrentFolder] = useState('');
    const [previousFolder, setPreviousFolder] = useState('');

    // Регистрация нового пользователя
    const handleRegister = async () => {
        try {
            const response = await axios.post('http://26.105.9.189:5000/register', { username, password });
            setMessage(response?.data?.message || 'Успех');
            setIsRegistering(false);
        } catch (error) {
            setMessage(error?.response?.data?.message || 'Ошибка при регистрации');
        }
    };

    // Вход пользователя
    const handleLogin = async () => {
        try {
            const response = await axios.post('http://26.105.9.189:5000/login', { username, password });
            setMessage(response?.data?.message || 'Успех');
            setIsLoggedIn(true);
            loadFoldersAndFiles();
        } catch (error) {
            setMessage(error?.response?.data?.message || 'Ошибка при входе');
        }
    };

    // Загрузка списка папок и файлов
    const loadFoldersAndFiles = async () => {
        try {
            const response = await axios.get('http://26.105.9.189:5000/files', {
                params: { username, currentFolder }
            });
            setFolders(response?.data?.folders || []);
            setFiles(response?.data?.files || []);
        } catch (error) {
            setMessage('Ошибка при загрузке папок или файлов');
        }
    };

    // Обновление списка папок и файлов
    const refreshFoldersAndFiles = () => {
        loadFoldersAndFiles();
    };

    // Загрузка файла
    const handleDownloadFile = async (fileName) => {
        try {
            const response = await axios.get('http://26.105.9.189:5000/download', {
                params: { username, fileName, currentFolder },
                responseType: 'blob',
            });
            const link = document.createElement('a');
            link.href = URL.createObjectURL(response.data);
            link.download = fileName;
            link.click();
        } catch (error) {
            setMessage('Ошибка при скачивании файла');
        }
    };

    // Переход в папку
    const handleEnterFolder = (folderName) => {
        setPreviousFolder(currentFolder); // Сохраняем текущую папку
        setCurrentFolder(folderName); // Переходим в выбранную папку
        loadFoldersAndFiles(); // Загружаем содержимое новой папки
    };

    // Переход назад
    const handleBack = () => {
        setCurrentFolder(previousFolder); // Возвращаемся в предыдущую папку
        setPreviousFolder(''); // Очищаем историю перехода
        loadFoldersAndFiles(); // Загружаем содержимое предыдущей папки
    };

    // Обработка загрузки файлов
    const handleFileUpload = async (event) => {
        const formData = new FormData();
        for (let i = 0; i < event.target.files.length; i++) {
            formData.append('files', event.target.files[i]);
        }
        try {
            const response = await axios.post('http://26.105.9.189:5000/upload', formData, {
                params: { username, currentFolder }
            });
            setMessage(response?.data?.message || 'Файлы успешно загружены');
            loadFoldersAndFiles(); // Обновляем список папок и файлов после загрузки
        } catch (error) {
            setMessage('Ошибка при загрузке файлов');
        }
    };

    return (
        <div className="container">
            <h1 className="title">Файловая система</h1>

            {/* Регистрация / Вход */}
            {!isLoggedIn && !isRegistering && (
                <div className="auth-section">
                    <h2>Вход</h2>
                    <input
                        className="input"
                        type="text"
                        placeholder="Имя пользователя"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        className="input"
                        type="password"
                        placeholder="Пароль"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button className="button" onClick={handleLogin}>Войти</button>
                    <button className="button" onClick={() => setIsRegistering(true)}>Зарегистрироваться</button>
                </div>
            )}

            {/* Регистрация */}
            {isRegistering && (
                <div className="auth-section">
                    <h2>Регистрация</h2>
                    <input
                        className="input"
                        type="text"
                        placeholder="Имя пользователя"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <input
                        className="input"
                        type="password"
                        placeholder="Пароль"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <button className="button" onClick={handleRegister}>Зарегистрироваться</button>
                </div>
            )}

            {/* Главная страница после входа */}
            {isLoggedIn && (
                <div className="dashboard">
                    <h2>Добро пожаловать, {username}</h2>
                    <button className="button" onClick={() => setIsLoggedIn(false)}>Выйти</button>

                    {/* Путь */}
                    <div className="path">
                        {currentFolder && (
                            <button className="button" onClick={handleBack}>Перейти назад</button>
                        )}
                        <span>{currentFolder ? `Текущая папка: ${currentFolder}` : 'Главная папка'}</span>
                    </div>

                    {/* Список папок и файлов */}
                    <div className="file-folder-list">
                        <h3>Папки и Файлы</h3>
                        <div className="file-folder-items">
                            {folders.length === 0 && files.length === 0 ? (
                                <p>Нет папок или файлов</p>
                            ) : (
                                <>
                                    {/* Папки */}
                                    {folders.map((folder) => (
                                        <div key={folder} className="file-folder-item">
                                            <span>{folder}</span>
                                            <button className="button" onClick={() => handleEnterFolder(folder)}>Перейти в папку</button>
                                        </div>
                                    ))}

                                    {/* Файлы */}
                                    {files.map((file) => (
                                        <div key={file} className="file-folder-item">
                                            <span>{file}</span>
                                            <button className="button" onClick={() => handleDownloadFile(file)}>Скачать файл</button>
                                        </div>
                                    ))}
                                </>
                            )}
                        </div>

                        <button className="button" onClick={refreshFoldersAndFiles}>Обновить список</button>
                    </div>

                    {/* Загрузка файлов */}
                    <div className="upload-section">
                        <h3>Загрузить файлы</h3>
                        <input
                            type="file"
                            multiple
                            onChange={handleFileUpload}
                            className="file-input"
                        />
                    </div>
                </div>
            )}

            <p>{message}</p>
        </div>
    );
};

export default App;
